import { AwsInputEventType } from './AwsEventType';
import { BrandId } from '@brandembassy/ts-types-platform/dist/brand/BrandId';
import { ChannelId } from '@brandembassy/ts-types-platform/dist/channel/ChannelId';
import { ChatWindowId } from '@brandembassy/ts-types-platform/dist/chatWindow/ChatWindowId';
import { CustomerIdentityIdOnExternalPlatform } from '@brandembassy/ts-types-platform/dist/endUser/EndUserIdentityId';
import { VisitorId } from '@brandembassy/ts-types-platform/dist/visitor/VisitorId';
import { VisitId } from '@brandembassy/ts-types-platform/dist/visit/visitId';
export interface BrandInput {
    id: BrandId;
}
export interface ChannelInput {
    id: ChannelId;
}
export interface DestinationInput {
    id: ChatWindowId;
}
export interface VisitorInput {
    id: VisitorId;
}
export interface VisitInput {
    id: VisitId;
}
export interface ConsumerIdentityInput {
    idOnExternalPlatform: CustomerIdentityIdOnExternalPlatform;
    firstName?: string;
    lastName?: string;
    image?: string;
}
export interface AwsInputEventData {
}
export interface AwsInputEvent<E extends AwsInputEventType, D extends AwsInputEventData> {
    eventType: E;
    brand: BrandInput;
    channel: ChannelInput;
    consumerIdentity?: ConsumerIdentityInput | {};
    data?: D;
    destination?: DestinationInput | {};
    visitor?: VisitorInput | {};
    visit?: VisitInput | {};
}
